//Hide footer
var onReadyDomElement = function(element, callback) {
  if (jQuery(element).length) {
    callback(jQuery(element));
  } else {
    setTimeout(function() {
      onReadyDomElement(element, callback);
    }, 100);
  }
};
// example
onReadyDomElement('div.rcx-sidebar-footer', function(e) {
  e.remove();
  console.log("footer removed");
});
